   function changefocus(e) {

    let isTabPressed = e.key === 'Tab' || e.keyCode === 9 || e.key === 'Escape' || e.keyCode === 27;

    if (!isTabPressed) {
      return;
    }

    if (e.key === 'Escape' || e.keyCode === 27) {
        e.preventDefault();

            document.querySelector('.opt-close-button').click();

    }
    else {
        if (e.shiftKey) { 

            if (document.activeElement === firstFocusableElement) {
                lastFocusableElement.focus();
                e.preventDefault();
            }
        } else { 

            if (document.activeElement === lastFocusableElement) { 
                firstFocusableElement.focus();
                e.preventDefault();
            }
        }

    }
  }

    


